Method.Source:
/// <summary>
/// Class entry point. The system will call this method when a designated menu 
/// is selected or when execution starts and this class is set as the startup class.
/// </summary>
/// <param name = "_args">The specified arguments.</param>
public static void main(Args _args)
{
    new FTBXTest_TryGetAvailableMemberStates().callInterface();
}

public void callInterface()
{
    FTBXECVIESInterfaceBase viesInterface = FTBXECVIESInterfaceBase::construct();

    viesInterface.validationResultUpdated += eventhandler(this.writeMessage);

    viesInterface.addCountry('DK', '25790936');
    viesInterface.addCountry('DK', '28498233');
    viesInterface.addCountry('DK', '13612870');
    viesInterface.invokeValidation();
}

public void writeMessage(FTBXECVIESCheckResult _result)
{
    Info(_result.entityName());
    Info(_result.entityAddressing());
    Info(strFmt("Is valid: %1", _result.isValid()));
}
